CREATE TABLE IF NOT EXISTS Classification (
    ID BIGINT PRIMARY KEY AUTO_INCREMENT,
    ORGID BIGINT NOT NULL,
    NAME VARCHAR(50) NOT NULL,
    MODULEID BIGINT NOT NULL,
    FORM_ID BIGINT,
    LINK_NAME VARCHAR(50) NOT NULL,
    DESCRIPTION VARCHAR(255),
    PARENT_CLASSIFICATION_ID BIGINT,
    EXTEND_ATTRIBUTE BOOLEAN,
    STATUS BOOLEAN DEFAULT TRUE,

    CONSTRAINT CLASSIFICATION_FK_MODULE_ID FOREIGN KEY (MODULEID) REFERENCES Modules (MODULEID),
    CONSTRAINT CLASSIFICATION_FK_FORM_ID FOREIGN KEY (FORM_ID) REFERENCES Forms (ID) ON DELETE SET NULL

);
CREATE TABLE IF NOT EXISTS Classification_Applied_Module (
    ID BIGINT PRIMARY KEY AUTO_INCREMENT,
    ORGID BIGINT NOT NULL,
    CLASSIFICATION_ID BIGINT NOT NULL,
    MODULE_ID BIGINT NOT NULL,

    CONSTRAINT CLASSIFICATIONAPPLIEDMODULE_FK_CLASSIFICATIONID FOREIGN KEY (CLASSIFICATION_ID) REFERENCES Classification (ID) ON DELETE CASCADE,
    CONSTRAINT CLASSIFICATIONAPPLIEDMODULE_FK_MODULEID FOREIGN KEY (MODULE_ID) REFERENCES Modules (MODULEID)
);

CREATE TABLE IF NOT EXISTS Classification_Attribute (
